public class Solution {
    public char findTheDifference(String s, String t) {
        char found = 0;

        int size = s.length();

        for (int i = 0; i < size; i++) {
            boolean charFound = false;

            for (int j = 0; j < t.length(); j++) {
                if (s.charAt(i) == t.charAt(j)) {
                    charFound = true;
                    break;
                }
            }

            if (!charFound) {
                found = s.charAt(i);
                break;
            }
        }

        if (found == 0) {
            found = t.charAt(size - 1); // Corrected the index to size - 1
        }

        return found;
    }

    public static void main(String[] args) {
        Solution solution = new Solution();
        String s = "";
        String t = "";
        System.out.println("Output: " + solution.findTheDifference(s, t));  // Print the output
    }
}
